INSERT INTO c_Package_Administration_Method
SELECT p.package_id, m.administer_method
FROM c_Package p, c_Administration_Method m
WHERE m.administer_method in ('IV INFUSION', 'IM')
AND substring(package_id,3,20) IN ('UGB4019','UGG4019','UGB4413','UGG4413')
AND NOT EXISTS (SELECT 1 
	FROM c_Package_Administration_Method m1
	WHERE m1.package_id = p.package_id
	AND m1.administer_method = m.administer_method
