/* Generate wording updates for generics, brands, formulations
SELECT -- g.generic_name, 
	'UPDATE c_Drug_Generic SET generic_name = dbo.fn_tallman(''' + [str] + ''') WHERE generic_rxcui = ''' + g.generic_rxcui + ''''
from c_Drug_Generic g
Join interfaces..rxnconso c on c.rxcui = g.generic_rxcui
where tty IN ('MIN','IN', 'PIN')
and g.generic_name != c.[str]

SELECT g.brand_name, 
	'UPDATE c_Drug_Brand SET brand_name = ''' + [str] + ''' WHERE brand_name_rxcui = ''' + g.brand_name_rxcui + ''''
from c_Drug_Brand g
Join interfaces..rxnconso c on c.rxcui = g.brand_name_rxcui
where tty = 'BN'
and g.brand_name != c.[str]


Formulations
Pen Injector -> Auto-Injector
Prefilled Applicator -> Prefilled Syringe
Inhalant Solution -> Inhalation Solution & Powder w/o Cartridge
SELECT -- f.form_descr, [str]
	'UPDATE c_Drug_Formulation SET form_descr = dbo.fn_tallman(''' + [str] + ''') WHERE form_rxcui = ''' + f.form_rxcui + ''''
from c_Drug_Formulation f
Join interfaces..rxnconso c on c.rxcui = f.form_rxcui
where form_tty IN ('SBD','SCD', 'SBD_PSN', 'SCD_PSN')
AND tty = right(form_tty, 3)
and f.form_descr != c.[str]
order by 1
*/ 
SELECT count(*) FROM interfaces..rxnconso c
WHERE tty = 'BN'
AND NOT EXiSTS (SELECT 1 FROM c_Drug_Brand b WHERE b.brand_name_rxcui = c.RXCUI)
-- 313

